import &#x7B; strict as assert &#x7D; from "assert";
import &#x7B; trimSpaces &#x7D; from "string-trim-spaces-only";

assert.deepEqual(trimSpaces("  aaa   "), &#x7B;
  res: "aaa",
  ranges: [
    [0, 2],
    [5, 8],
  ],
&#x7D;);

assert.deepEqual(trimSpaces("   \t  zz   \n    "), &#x7B;
  res: "\t  zz   \n",
  ranges: [
    [0, 3],
    [12, 16],
  ],
&#x7D;);
